data class PantiniLenta (
    val ticker: String,
) {
    var prints: MutableList<PantiniPrint> = mutableListOf()

    fun create(jsonArray: JSONArray) {
        for (i in 0 until jsonArray.length()) {
            val item = jsonArray.get(i).toString()
            val data = item.split("|")

            if (data.size == 6) {
                val print = PantiniPrint(data[0].toDouble(), data[1].toDouble(), data[2], data[3], data[4].toDouble(), data[5].toDouble())
                prints.add(print)
            }
        }
    }
}